import React, {useState, useEffect} from 'react'



const Options = () => {

    const weight = [
        {value: '', name: 'Please Select'},   
        {value: 'gram', name: 'Gram'},
        {value: 'kilogram', name: 'Kilogram'},        
    ]
    const colors = [
        {value: '', name: 'Please Select'},   
        {value: 'red', name: 'Red'},
        {value: 'blue', name: 'Blue'},
        {value: 'black', name: 'Black'},
    ]
    const size = [
        {value: '', name: 'Please Select'},       
        {value: 'dimension', name: 'Dimensions'},       
    ]

    const [firstOption, setFirstOption] = useState([
        {value: 'weight', name: 'Weight'},
        {value: 'color', name: 'Color'},
        {value: 'size', name: 'Size'},
    ]);

    const [topOption, setTopOption] = useState(weight);   
    const [middleOption, setMiddleOption] = useState(weight);   
    const [middleBottomOption, setMiddleBottomOption] = useState(weight);   
    const [bottomOption, setBottomOption] = useState(weight);       
    const [input, setInput] = useState({
        top: '',
        middle: '',
        middleBottom: '',
        bottom: ''
    });   
    const [dependencyInput, setDependencyInput] = useState({
        top: '',
        middle: '',
        middleBottom: '',
        bottom: ''
    });


    const handleChange = (e) => {      
        setInput({...input, [e.target.name]: e.target.value});         

        if(e.target.value === 'weight') 
        {
            setTopOption(weight);
        }
        else if(e.target.value === 'color')
        {
            setTopOption(colors);
        }
        else if(e.target.value === 'size')
        {
            setTopOption(size);
        }
        else {
            setTopOption(weight);
        }
    }

    const handleChangeMiddle = (e) => {
        setInput({...input, [e.target.name]: e.target.value}); 

        if(e.target.value === 'weight') 
        {
            setMiddleOption(weight);
        }
        else if(e.target.value === 'color')
        {
            setMiddleOption(colors);
        }
        else if(e.target.value === 'size')
        {
            setMiddleOption(size);
        }
        else {
            setMiddleOption(weight);
        }
    }

    const handleChangeBottomMiddle = (e) => {
        setInput({...input, [e.target.name]: e.target.value}); 

        if(e.target.value === 'weight') 
        {
            setMiddleBottomOption(weight);
        }
        else if(e.target.value === 'color')
        {
            setMiddleBottomOption(colors);
        }
        else if(e.target.value === 'size')
        {
            setMiddleBottomOption(size);
        }
        else {
            setMiddleBottomOption(weight);
        }
    }

    const handleChangeBottom = (e) => {
        setInput({...input, [e.target.name]: e.target.value}); 

        if(e.target.value === 'weight') 
        {
            setBottomOption(weight);
        }
        else if(e.target.value === 'color')
        {
            setBottomOption(colors);
        }
        else if(e.target.value === 'size')
        {
            setBottomOption(size);
        }
        else {
            setBottomOption(weight);
        }
    }


    const handleChangeSelectTop = (e) => {        
        setDependencyInput({...input, [e.target.name]: e.target.value});    
    }
    const handleChangeSelectMiddle = (e) => {        
        setDependencyInput({...input, [e.target.name]: e.target.value});    
    }
    const handleChangeSelectMiddleBottom = (e) => {        
        setDependencyInput({...input, [e.target.name]: e.target.value});    
    }
    const handleChangeSelectBottom = (e) => {        
        setDependencyInput({...input, [e.target.name]: e.target.value});    
    }

    useEffect(()=>{
        setDependencyInput('');
    },[input])



console.log('input',input);
console.log('dependencyInput',dependencyInput);


  return (
    <>
        <div className='Options'>            
            <div className='container structure'>
                <div className='Top input-align'>
                <h3>Top</h3>
                    <div className='d-flex'>
                        <select id='top' className='form-control' name='top' value={input.top} onChange={(e)=>handleChange(e)}>
                            {firstOption.map((item,i) => (                                    
                                    <option key={i} value={item.value}>{item.name}</option>                                                                                                                                        
                            ))}                                
                        </select>
                        <select className='form-control' value={dependencyInput.top}  onChange={(e)=>handleChangeSelectTop(e)}>
                            {topOption.map((item)=>(
                                <option value={item.value}>{item.name}</option> 
                            ))}                                
                        </select>
                        {dependencyInput.top == 'dimension' ? <input type='number' className='form-control' /> : ''}                            
                        {dependencyInput.top == 'gram' ? <input type='number' className='form-control' /> : ''}                            
                        {dependencyInput.top == 'kilogram' ? <input type='number' className='form-control' /> : ''}                            
                    </div>
                </div>

                <div className='Middle input-align'>
                    <h3>Middle</h3>
                    <div className='d-flex'>
                        <select id='top' className='form-control' name='middle' value={input.middle} onChange={(e)=>handleChangeMiddle(e)}>
                            {firstOption.map((item,i) => (
                                <>
                                    <option key={i} value={item.value}>{item.name}</option>                                       
                                </>                            
                            ))}                                
                        </select>
                        <select className='form-control' name='middle' value={dependencyInput.middle}  onChange={(e)=>handleChangeSelectMiddle(e)}>
                            {middleOption.map((item)=>(
                                <option value={item.value}>{item.name}</option>                               
                            ))}                                
                        </select>
                        {dependencyInput.middle == 'dimension' ? <input type='number' className='form-control' /> : ''}                            
                        {dependencyInput.middle == 'gram' ? <input type='number' className='form-control' /> : ''}                            
                        {dependencyInput.middle == 'kilogram' ? <input type='number' className='form-control' /> : ''}    
                    </div>
                </div>

                <div className='Bottom-middle input-align'>
                    <h3>Bottom-middle</h3>
                    <div className='d-flex'>
                        <select id='top' className='form-control' name='middleBottom' value={input.middleBottom} onChange={(e)=>handleChangeBottomMiddle(e)}>
                            {firstOption.map((item,i) => (
                                <>
                                    <option key={i} value={item.value}>{item.name}</option>                                       
                                </>                            
                            ))}                                
                        </select>
                        <select className='form-control' name='middleBottom' value={dependencyInput} onChange={(e)=>handleChangeSelectMiddleBottom(e)} >
                            {middleBottomOption.map((item)=>(
                                <option value={item.value}>{item.name}</option>                               
                            ))}                                
                        </select>
                        <input type='text' className='form-control' />
                    </div>
                </div>

                <div className='Bottom input-align'>
                    <h3>Bottom</h3>
                    <div className='d-flex'>
                        <select id='top' className='form-control' name='bottom' value={input.bottom} onChange={(e)=>handleChangeBottom(e)}>
                            {firstOption.map((item,i) => (                               
                                <option key={i} value={item.value}>{item.name}</option>                                                                                          
                            ))}                                
                        </select>
                        <select className='form-control' name='bottom' value={dependencyInput} onChange={(e)=>handleChangeSelectBottom(e)} >
                            {bottomOption.map((item)=>(
                                <option value={item.value}>{item.name}</option>                               
                            ))}                                
                        </select>
                        <input type='text' className='form-control' />
                    </div>
                </div>   
            </div>                         
        </div>
    </>
  )
}

export default Options
