//         topOption.map((item)=>{    
//             setInput(item.value);
//         })
//    },[topOption])   




  return (
    <>
        <div className='Options'>
            {/* <div className=''> */}
                <div className='container structure'>
                    <div className='Top input-align'>
                    <h3>Top</h3>
                        <div className='d-flex'>
                            <select id='top' className='form-control' name='top' onChange={(e)=>handleChange(e)}>
                                {firstOption.map((item,i) => (                                    
                                        <option key={i} value={item.value}>{item.name}</option>                                                                                                                                        
                                ))}                                
                            </select>
                            <select className='form-control' name='topOption'  onChange={(e)=>checkOptions(e)}>
                                {topOption.map((item)=>(
                                    <option value={item.value}>{item.name}</option> 
                                ))}                                
                            </select>
                            {input == 'dimension' ? <input type='text' className='form-control' /> : ''}                            
                        </div>
                    </div>

                    <div className='Middle input-align'>
                        <h3>Middle</h3>
                        <div className='d-flex'>
                            <select id='top' className='form-control' onChange={(e)=>handleChangeMiddle(e)}>
                                {firstOption.map((item,i) => (
                                    <>
                                        <option key={i} value={item.value}>{item.name}</option>                                       
                                    </>                            
                                ))}                                
                            </select>
                            <select className='form-control'>
                                {middleOption.map((item)=>(
                                    <option value={item.value}>{item.name}</option>                               
                                ))}                                
                            </select>
                            <input type='text' className='form-control' />
                        </div>
                    </div>

                    <div className='Bottom-middle input-align'>
                        <h3>Bottom-middle</h3>
                        <div className='d-flex'>
                            <select id='top' className='form-control' onChange={(e)=>handleChangeBottomMiddle(e)}>
                                {firstOption.map((item,i) => (
                                    <>
                                        <option key={i} value={item.value}>{item.name}</option>                                       
                                    </>                            
                                ))}                                
                            </select>
                            <select className='form-control' >
                                {middleBottomOption.map((item)=>(
                                    <option value={item.value}>{item.name}</option>                               
                                ))}                                
                            </select>
                            <input type='text' className='form-control' />
                        </div>
                    </div>

                    <div className='Bottom input-align'>
                        <h3>Bottom</h3>
                        <div className='d-flex'>
                            <select id='top' className='form-control' onChange={(e)=>handleChangeBottom(e)}>
                                {firstOption.map((item,i) => (
                                    <>
                                        <option key={i} value={item.value}>{item.name}</option>                                       
                                    </>                            
                                ))}                                
                            </select>
                            <select className='form-control' >
                                {bottomOption.map((item)=>(
                                    <option value={item.value}>{item.name}</option>                               
                                ))}                                
                            </select>
                            <input type='text' className='form-control' />
                        </div>
                    </div>   
                </div>             
            {/* </div> */}
        </div>
    </>
  )
}

export default Options
