import React, {useState, useEffect} from 'react'



const Options = ({value, setValue}) => {

    // const weight = [
    //     {value: '', name: 'Please Select'},   
    //     {value: 'gram', name: 'Gram'},
    //     {value: 'kilogram', name: 'Kilogram'},        
    // ]
    const colors = [
        {value: '', name: 'Please Select'},   
        {value: 'red', name: 'Red'},
        {value: 'blue', name: 'Blue'},
        {value: 'black', name: 'Black'},
    ]
    const size = [
        {value: '', name: 'Please Select'},       
        {value: 'dimension', name: 'Dimensions'},       
    ]

    const [firstOption, setFirstOption] = useState([       
        {value: 'color', name: 'Color'},
        {value: 'size', name: 'Size'},
    ]);

    const [topOption, setTopOption] = useState(colors);   
    const [middleOption, setMiddleOption] = useState(colors);   
    const [middleBottomOption, setMiddleBottomOption] = useState(colors);   
    const [bottomOption, setBottomOption] = useState(colors);       
    const [input, setInput] = useState({
        top: 'color',
        middle: 'color',
        middleBottom: 'color',
        bottom: 'color'
    });   
    const [dependencyInput, setDependencyInput] = useState({
        topInput: {color: '', height: '', width: ''},
        middleInput: '',
        middleBottomInput: '',
        bottomInput: ''
    });


    const handleChange = (e) => {      
        setInput({...input, [e.target.name]: e.target.value});         

        if(e.target.value === 'color')
        {
            setTopOption(colors);
        }
        else if(e.target.value === 'size')
        {
            setTopOption(size);
        }
        else {
            setTopOption(colors);
        }
    }

    const handleChangeMiddle = (e) => {
        setInput({...input, [e.target.name]: e.target.value}); 

        if(e.target.value === 'color')
        {
            setMiddleOption(colors);
        }
        else if(e.target.value === 'size')
        {
            setMiddleOption(size);
        }
        else {
            setMiddleOption(colors);
        }
    }

    const handleChangeBottomMiddle = (e) => {
        setInput({...input, [e.target.name]: e.target.value}); 

        if(e.target.value === 'color')
        {
            setMiddleBottomOption(colors);
        }
        else if(e.target.value === 'size')
        {
            setMiddleBottomOption(size);
        }
        else {
            setMiddleBottomOption(colors);
        }
    }

    const handleChangeBottom = (e) => {
        setInput({...input, [e.target.name]: e.target.value}); 

        if(e.target.value === 'color')
        {
            setBottomOption(colors);
        }
        else if(e.target.value === 'size')
        {
            setBottomOption(size);
        }
        else {
            setBottomOption(colors);
        }
    }


    const handleChangeSelectTop = (e) => {        
        setDependencyInput({...dependencyInput,[e.target.name]: e.target.value});  
        setValue((prev)=>({...prev, color: e.target.value}));  
    }
    const handleChangeSelectMiddle = (e) => {        
        setDependencyInput({...dependencyInput, [e.target.name]: e.target.value});    
    }
    const handleChangeSelectMiddleBottom = (e) => {        
        setDependencyInput({...dependencyInput, [e.target.name]: e.target.value});    
    }
    const handleChangeSelectBottom = (e) => {        
        setDependencyInput({...dependencyInput, [e.target.name]: e.target.value});    
    }

    // useEffect(()=>{
    //     setDependencyInput({...dependencyInput, topInput:''});        
    // },[input.top])

    useEffect(()=>{
        setDependencyInput({...dependencyInput, middleInput:''});    
    },[input.middle])

    useEffect(()=>{
        setDependencyInput({...dependencyInput, middleBottomInput:''});          
    },[input.middleBottom])

    useEffect(()=>{
        setDependencyInput({...dependencyInput, bottomInput:''});             
    },[input.bottom])


    const handleValue = (e) => {
        console.log('e.target.value',e.target.value);
        setValue((prev)=>({...prev, [e.target.name]: e.target.value}));  
    }

    // console.log('input',input);
    console.log('dependencyInput',dependencyInput);


  return (
    <>
        <div className='Options'>            
            <div className='container structure'>
                <div className='Top input-align'>
                <h3>Top</h3>
                    <div className='d-flex'>
                        <select id='top' className='form-control' name='top' value={input.top} onChange={(e)=>handleChange(e)}>
                            {firstOption.map((item,i) => (                                    
                                    <option key={i} value={item.value}>{item.name}</option>                                                                                                                                        
                            ))}                                
                        </select>
                        { input.top == 'color' ?                           

                            <select className='form-control' name='color' value={dependencyInput.topInput.color}  onChange={(e)=>handleChangeSelectTop(e)}>
                                {topOption.map((item)=>(
                                    <option value={item.value}>{item.name}</option> 
                                ))}                                
                            </select>                           
                          : 
                            <>
                                L: <input type='number' name='height' value={dependencyInput.topInput.height} onChange={(e)=>handleValue(e)} className='form-control' />
                                W: <input type='number' name='width' onChange={(e)=>handleValue(e)} className='form-control' /> 
                            </>                         
                        }
                    </div>
                </div>

                <div className='Middle input-align'>
                    <h3>Middle</h3>
                    <div className='d-flex'>
                        <select id='top' className='form-control' name='middle' value={input.middle} onChange={(e)=>handleChangeMiddle(e)}>
                            {firstOption.map((item,i) => (
                                <>
                                    <option key={i} value={item.value}>{item.name}</option>                                       
                                </>                            
                            ))}                                
                        </select>
                        <select className='form-control' name='middleInput' value={dependencyInput.middleInput}  onChange={(e)=>handleChangeSelectMiddle(e)}>
                            {middleOption.map((item)=>(
                                <option value={item.value}>{item.name}</option>                               
                            ))}                                
                        </select>
                        {dependencyInput.middleInput == 'dimension' ? <input type='number' className='form-control' /> : ''}                            
                        {dependencyInput.middleInput == 'gram' ? <input type='number' className='form-control' /> : ''}                            
                        {dependencyInput.middleInput == 'kilogram' ? <input type='number' className='form-control' /> : ''}    
                    </div>
                </div>

                <div className='Bottom-middle input-align'>
                    <h3>Bottom-middle</h3>
                    <div className='d-flex'>
                        <select id='top' className='form-control' name='middleBottom' value={input.middleBottom} onChange={(e)=>handleChangeBottomMiddle(e)}>
                            {firstOption.map((item,i) => (
                                <>
                                    <option key={i} value={item.value}>{item.name}</option>                                       
                                </>                            
                            ))}                                
                        </select>
                        <select className='form-control' name='middleBottomInput' value={dependencyInput.middleBottomInput} onChange={(e)=>handleChangeSelectMiddleBottom(e)} >
                            {middleBottomOption.map((item)=>(
                                <option value={item.value}>{item.name}</option>                               
                            ))}                                
                        </select>
                        {dependencyInput.middleBottomInput == 'dimension' ? <input type='number' className='form-control' /> : ''}                            
                        {dependencyInput.middleBottomInput == 'gram' ? <input type='number' className='form-control' /> : ''}                            
                        {dependencyInput.middleBottomInput == 'kilogram' ? <input type='number' className='form-control' /> : ''}   
                    </div>
                </div>

                <div className='Bottom input-align'>
                    <h3>Bottom</h3>
                    <div className='d-flex'>
                        <select id='top' className='form-control' name='bottom' value={input.bottom} onChange={(e)=>handleChangeBottom(e)}>
                            {firstOption.map((item,i) => (                               
                                <option key={i} value={item.value}>{item.name}</option>                                                                                          
                            ))}                                
                        </select>
                        <select className='form-control' name='bottomInput' value={dependencyInput.bottomInput} onChange={(e)=>handleChangeSelectBottom(e)} >
                            {bottomOption.map((item)=>(
                                <option value={item.value}>{item.name}</option>                               
                            ))}                                
                        </select>
                        {dependencyInput.bottomInput == 'dimension' ? <input type='number' className='form-control' /> : ''}                            
                        {dependencyInput.bottomInput == 'gram' ? <input type='number' className='form-control' /> : ''}                            
                        {dependencyInput.bottomInput == 'kilogram' ? <input type='number' className='form-control' /> : ''}   
                    </div>
                </div>   
            </div>                         
        </div>
    </>
  )
}

export default Options
